select ID, name, address, phone, email, credit, note FROM customer WHERE (name LIKE CONCAT(CONCAT('%', #{key}),'%') OR address LIKE CONCAT(CONCAT('%', #{key}),'%') OR credit LIKE CONCAT(CONCAT('%', #{key}),'%'))
AND ID = #{id}
limit #{prev} , #{next}
